        localStorage.removeItem(key);
      });

      Object.entries(importData.data).forEach(([key, value]) => {
        if (value !== undefined) {
          localStorage.setItem(key, JSON.stringify(value));
        }
      });

      setShowImportConfirm(false);
      setImportFile(null);
      setImportData(null);

